import kotlinx.interop.wasm.dom.document
import kotlinx.wasm.jsinterop.JsValue
import kotlinx.wasm.jsinterop.setter
import org.mvnsearch.wasm.demo.Welcome

@Retain
fun hello() {
   println("Hello from retain")
}

@SymbolName("alert")
external fun alert(message: Any?): Unit

fun JsValue.onclick(lambda: kotlinx.wasm.jsinterop.KtFunction<kotlin.Unit> ) {
    this.setter("onclick",lambda);
}

fun main() {
    val demo = document.getElementById("Demo")
    demo.setter("href", "#")
    demo.onclick {
        println("mouse clicked!")
    }
    println(Welcome().hello())
}
